function Form() {
  const [form, setForm] = useState({ name: "", surname: "" });

  handleInputChange = (event) => {
    const { target } = event;
    const { value } = target;
    const { name } = target;

    setForm()
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { name, surname } = this.state;
    const result = `Hi ${name} ${surname}`;
    alert(result);
  };
  return (
    <form onSubmit={this.handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          name="name"
          value={name}
          onChange={this.handleInputChange}
        />
      </label>
      <br />
      <label>
        Surname:
        <input
          type="text"
          name="surname"
          value={surname}
          onChange={this.handleInputChange}
        />
      </label>
      <br />
      <input type="submit" value="Submit" />
    </form>
  );
}

export default Form;
